import { useEffect, useState } from "react";
import Formulario from "./componentes/Form"

const App = () => {
    const [pokemons, setPokemons] = useState([]);
    const [carga, setCarga] = useState(20); // Usar el estado para controlar la cantidad de Pokémon cargados
    const [busqueda, setBusqueda] = useState('');

    const buscarPokemons = async () => {
        try {
            const response = await fetch(`https://pokeapi.co/api/v2/pokemon/?offset=${carga}&limit=${carga}`);
            const data = await response.json();

            const pokemonData = await Promise.all(data.results.map(async (pokemon) => {
                const pokemonResponse = await fetch(pokemon.url);
                const pokemonDetails = await pokemonResponse.json();
                return {
                    name: pokemonDetails.name,
                    image: pokemonDetails.sprites.front_default,
                    number: pokemonDetails.order,
                    exp: pokemonDetails.base_experience,
                    types: pokemonDetails.types,
                };
            }));

            setPokemons(pokemonData);
        } catch (error) {
            console.error(error);
        }
    };

    const buscar = async () => {
        try {
            const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${busqueda}`);
            const data = await response.json();

            setPokemons([{
                name: data.name,
                image: data.sprites.front_default,
                number: data.order,
                exp: data.base_experience,
                types: data.types,
            }]);
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        busqueda == '' ? buscarPokemons() : buscar()
    }, [busqueda, carga]);

    const cargarMasPokemon = () => {
        // Incrementar la variable carga y llamar a fetchData() nuevamente
        setCarga(carga + 10);
    };

    return (
        <div>
            <header>
                <h2>Buscar</h2>
                <Formulario setBusqueda={setBusqueda}/>
            </header>
            <main>
                <div></div>
                <div>
                    <button onClick={cargarMasPokemon}>Cargar más</button>
                </div>
            </main>
        </div>
    );
};

export default App;
