import { useEffect, useState } from "react";
import Input from "./componentes/Input"

let carga = 20






const App = () =>{

    const [ pokemons, setPokemons ] = useState([])

    const fetchData = async() => {
        try {
            const response = await fetch(`https://pokeapi.co/api/v2/pokemon/?offset=20&limit=20`);
            const data = await response.json();

            const pokemonData = await Promise.all(data.results.map(async pokemon => {
                const pokemonResponse = await fetch(pokemon.url)
                const pokemonDetails = await pokemonResponse.json()
                return{
                    name: pokemonDetails.name,
                    image: pokemonDetails.sprites.front_default,
                    number: pokemonDetails.order,
                    exp: pokemonDetails.base_experience,
                    types: pokemonDetails.types
                }
            }))

            setPokemons(pokemonData)

        } catch (error) {
            console.error(error)
        }
    }

    useEffect(()=>{
        fetchData()
    },[carga])


    return(
        <div>
            <header>
                <h2>Buscar</h2>
                <Input></Input>
            </header>
            <main>
                <div>
                    {pokemons.map((pokemon, index) => (
                        <div key={index}>
                            <img src={pokemon.image} />
                            <div>
                                <p>#{pokemon.number}</p>
                                <p>Exp: {pokemon.exp}</p>
                            </div>
                            <p>{pokemon.name}</p>
                            <div>
                            {pokemon.types.map(e => (<p>{e.type.name}</p>))}
                            </div>
                        </div>
                    ))}
                </div>
                <div>
                    <button onClick={() => carga + 10}>Cargar mas</button>
                </div>
            </main>
        </div>
    )
}

export default App
